// ForgotPassword.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import '../App.css'; // Dosya yolunu düzeltin

const ForgotPassword = () => {
  const [username, setUsername] = useState('');
  const [securityAnswer, setSecurityAnswer] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [step, setStep] = useState(1); // 1: Kullanıcı adı, 2: Güvenlik sorusu cevabı ve yeni şifre
  const [securityQuestion, setSecurityQuestion] = useState('');
  const navigate = useNavigate();

  const handleUsernameSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/forgot-password', { username });
      if (res.data && res.data.securityQuestion) {
        setSecurityQuestion(res.data.securityQuestion);
        setStep(2);
      } else {
        alert('User not found');
      }
    } catch (err) {
      console.error(err.response ? err.response.data : err.message);
      alert('User not found');
    }
  };

  const handlePasswordReset = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/reset-password', {
        username,
        securityAnswer,
        newPassword
      });
      if (res.status === 200) {
        alert('Password reset successful');
        navigate('/login');
      } else {
        alert('Security answer is incorrect');
      }
    } catch (err) {
      console.error(err.response ? err.response.data : err.message);
      alert('Security answer is incorrect');
    }
  };

  return (
    <div className="container">
      {step === 1 ? (
        <div>
          <h2>Forgot Password</h2>
          <form onSubmit={handleUsernameSubmit}>
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <button type="submit">Next</button>
          </form>
          <p>
            Remember your password? <Link to="/login">Login</Link>
          </p>
        </div>
      ) : (
        <div>
          <h2>Security Question</h2>
          <p>{securityQuestion}</p>
          <form onSubmit={handlePasswordReset}>
            <input
              type="text"
              placeholder="Security Answer"
              value={securityAnswer}
              onChange={(e) => setSecurityAnswer(e.target.value)}
            />
            <input
              type="password"
              placeholder="New Password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
            />
            <button type="submit">Reset Password</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default ForgotPassword;
