import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

const Quiz = ({ limit }) => {
  const [words, setWords] = useState([]);
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [score, setScore] = useState(0);

  const fetchQuiz = useCallback(async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      console.error('No token found');
      return;
    }
    try {
      const res = await axios.get(`http://localhost:5000/api/quiz?limit=${limit}`, {
        headers: {
          'x-auth-token': token
        }
      });
      console.log('Fetched quiz:', res.data);  // API'den gelen yan覺t覺 kontrol et
      setWords(res.data);
    } catch (err) {
      console.error('Fetch quiz error:', err.response.data);
    }
  }, [limit]);

  useEffect(() => {
    fetchQuiz();
  }, [fetchQuiz]);

  const handleAnswer = async (isCorrect) => {
    const token = localStorage.getItem('token');
    if (!token) {
      console.error('No token found');
      return;
    }
    try {
      const wordId = words[currentWordIndex].id;
      await axios.post('http://localhost:5000/api/quiz', { wordId, correct: isCorrect }, {
        headers: {
          'x-auth-token': token
        }
      });

      if (isCorrect) {
        setScore(score + 1);
      }

      const nextIndex = currentWordIndex + 1;
      if (nextIndex < words.length) {
        setCurrentWordIndex(nextIndex);
      } else {
        alert(`Quiz completed! Your score: ${score}/${words.length}`);
        setCurrentWordIndex(0);
        setScore(0);
        fetchQuiz();
      }
    } catch (err) {
      console.error('Handle answer error:', err.response.data);
    }
  };

  if (words.length === 0) {
    return <div>Loading...</div>;
  }

  const currentWord = words[currentWordIndex];

  return (
    <div>
      <h1>Quiz</h1>
      <p>{currentWord.english}</p>
      <button onClick={() => handleAnswer(true)}>Correct</button>
      <button onClick={() => handleAnswer(false)}>Incorrect</button>
    </div>
  );
};

export default Quiz;
