import React, { useState, useEffect } from 'react';
import axios from 'axios';

const WordList = () => {
  const [words, setWords] = useState([]);

  useEffect(() => {
    const fetchWords = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/words');
        setWords(res.data);
      } catch (err) {
        console.error(err.response.data);
      }
    };

    fetchWords();
  }, []);

  return (
    <div>
      <h1>Word List</h1>
      <ul>
        {words.map(word => (
          <li key={word.id}>
            <h2>{word.english} - {word.turkish}</h2>
            <p>{word.sentences}</p>
            {word.image && <img src={word.image} alt={word.english} width="100" />}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default WordList;
