import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import './Auth.css';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    securityQuestion: '',
    securityAnswer: ''
  });

  const { username, password, securityQuestion, securityAnswer } = formData;
  const navigate = useNavigate();

  const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/register', { username, password, securityQuestion, securityAnswer });
      console.log('Registered successfully:', res.data);
      navigate('/login');
    } catch (err) {
      console.error('Register error:', err.response.data);
    }
  };

  return (
    <div className="auth-container">
      <form onSubmit={onSubmit} className="auth-form">
        <h2>Register</h2>
        <div>
          <label htmlFor="username">Username</label>
          <input type="text" id="username" name="username" value={username} onChange={onChange} required />
        </div>
        <div>
          <label htmlFor="password">Password</label>
          <input type="password" id="password" name="password" value={password} onChange={onChange} required />
        </div>
        <div>
          <label htmlFor="securityQuestion">Security Question</label>
          <input type="text" id="securityQuestion" name="securityQuestion" value={securityQuestion} onChange={onChange} required />
        </div>
        <div>
          <label htmlFor="securityAnswer">Security Answer</label>
          <input type="text" id="securityAnswer" name="securityAnswer" value={securityAnswer} onChange={onChange} required />
        </div>
        <button type="submit">Register</button>
        <p>
          Already have an account? <Link to="/login">Login</Link>
        </p>
      </form>
    </div>
  );
};

export default Register;
