import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Quiz = () => {
    const [questions, setQuestions] = useState([]);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [options, setOptions] = useState([]);
    const [feedback, setFeedback] = useState('');

    useEffect(() => {
        const fetchQuizWords = async () => {
            try {
                const res = await axios.get('http://localhost:5000/api/quiz', {
                    headers: {
                        'x-auth-token': localStorage.getItem('token')
                    }
                });
                setQuestions(res.data);
                generateOptions(res.data[0]);
            } catch (err) {
                console.error('Fetch quiz error:', err.response.data);
            }
        };

        fetchQuizWords();
    }, []);

    const generateOptions = async (question) => {
        try {
            // Fetch three random options
            const res = await axios.get('http://localhost:5000/api/words/random', {
                headers: {
                    'x-auth-token': localStorage.getItem('token')
                }
            });

            // Ensure the correct answer is included in the options
            const options = res.data.slice(0, 3);
            options.push(question);
            setOptions(shuffle(options));
        } catch (err) {
            console.error('Fetch random words error:', err.response.data);
        }
    };

    const shuffle = (array) => {
        for (let i = array.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [array[i], array[j]] = [array[j], array[i]];
        }
        return array;
    };

    const handleAnswer = (option) => {
        const question = questions[currentQuestionIndex];
        if (option.turkish === question.turkish) {
            setFeedback('Correct');
        } else {
            setFeedback(`Incorrect. The correct answer was: ${question.turkish}`);
        }

        setTimeout(() => {
            if (currentQuestionIndex + 1 < questions.length) {
                setCurrentQuestionIndex(currentQuestionIndex + 1);
                setFeedback('');
                generateOptions(questions[currentQuestionIndex + 1]);
            } else {
                setFeedback('You have completed the quiz.');
            }
        }, 2000);
    };

    if (!questions.length) {
        return <div>Loading...</div>;
    }

    const question = questions[currentQuestionIndex];

    return (
        <div>
            <h1>Quiz</h1>
            <p>{question.english}</p>
            {options.map((option, index) => (
                <button key={index} onClick={() => handleAnswer(option)}>
                    {option.turkish}
                </button>
            ))}
            {feedback && <p>{feedback}</p>}
        </div>
    );
};

export default Quiz;
