import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import './Auth.css';

const ForgotPassword = () => {
  const [step, setStep] = useState(1);
  const [username, setUsername] = useState('');
  const [securityQuestion, setSecurityQuestion] = useState('');
  const [securityAnswer, setSecurityAnswer] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleUsernameSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/forgot-password', { username });
      setSecurityQuestion(res.data.securityQuestion);
      setStep(2);
    } catch (err) {
      setError(err.response.data.msg);
    }
  };

  const handleSecurityAnswerSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/auth/verify-answer', { username, securityAnswer });
      setStep(3);
    } catch (err) {
      setError(err.response.data.msg);
    }
  };

  const handleNewPasswordSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/auth/reset-password', { username, newPassword });
      navigate('/login');
    } catch (err) {
      setError(err.response.data.msg);
    }
  };

  return (
    <div className="auth-container">
      <form onSubmit={step === 1 ? handleUsernameSubmit : step === 2 ? handleSecurityAnswerSubmit : handleNewPasswordSubmit} className="auth-form">
        <h2>Forgot Password</h2>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {step === 1 && (
          <div>
            <label htmlFor="username">Username</label>
            <input type="text" id="username" name="username" value={username} onChange={(e) => setUsername(e.target.value)} required />
          </div>
        )}
        {step === 2 && (
          <div>
            <label>{securityQuestion}</label>
            <input type="text" name="securityAnswer" value={securityAnswer} onChange={(e) => setSecurityAnswer(e.target.value)} required />
          </div>
        )}
        {step === 3 && (
          <div>
            <label htmlFor="newPassword">New Password</label>
            <input type="password" id="newPassword" name="newPassword" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} required />
          </div>
        )}
        <button type="submit">Submit</button>
        <p>
          Remembered your password? <Link to="/login">Login</Link>
        </p>
      </form>
    </div>
  );
};

export default ForgotPassword;
