import React, { useState } from 'react';
import axios from 'axios';

const AddWord = () => {
  const [english, setEnglish] = useState('');
  const [turkish, setTurkish] = useState('');
  const [pronunciation, setPronunciation] = useState('');
  const [sentence1, setSentence1] = useState('');
  const [sentence2, setSentence2] = useState('');
  const [sentence3, setSentence3] = useState('');
  const [image, setImage] = useState('');

  const onSubmit = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem('token');

    try {
      const response = await axios.post(
        'http://localhost:5000/api/words',
        {
          english,
          turkish,
          pronunciation,
          sentences1: sentence1,
          sentences2: sentence2,
          sentences3: sentence3,
          image
        },
        {
          headers: {
            'Content-Type': 'application/json',
            'x-auth-token': token
          }
        }
      );
      console.log(response.data);
    } catch (error) {
      console.error(error.response.data);
    }
  };

  return (
    <form onSubmit={onSubmit}>
      <input
        type="text"
        placeholder="English"
        value={english}
        onChange={(e) => setEnglish(e.target.value)}
      />
      <input
        type="text"
        placeholder="Turkish"
        value={turkish}
        onChange={(e) => setTurkish(e.target.value)}
      />
      <input
        type="text"
        placeholder="Pronunciation"
        value={pronunciation}
        onChange={(e) => setPronunciation(e.target.value)}
      />
      <input
        type="text"
        placeholder="Sentence 1"
        value={sentence1}
        onChange={(e) => setSentence1(e.target.value)}
      />
      <input
        type="text"
        placeholder="Sentence 2"
        value={sentence2}
        onChange={(e) => setSentence2(e.target.value)}
      />
      <input
        type="text"
        placeholder="Sentence 3"
        value={sentence3}
        onChange={(e) => setSentence3(e.target.value)}
      />
      <input
        type="text"
        placeholder="Image URL"
        value={image}
        onChange={(e) => setImage(e.target.value)}
      />
      <button type="submit">Add Word</button>
    </form>
  );
};

export default AddWord;
