import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const ForgotPassword = () => {
    const [username, setUsername] = useState('');
    const [securityQuestion, setSecurityQuestion] = useState('');
    const [securityAnswer, setSecurityAnswer] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [step, setStep] = useState(1); // 1: username input, 2: security question, 3: new password
    const history = useHistory();

    const handleUsernameSubmit = async () => {
        try {
            const response = await axios.post('http://localhost:5000/api/auth/forgot-password', { username });
            setSecurityQuestion(response.data.securityQuestion);
            setStep(2);
        } catch (error) {
            alert('User not found. Please register.');
        }
    };

    const handleSecurityAnswerSubmit = async () => {
        try {
            await axios.post('http://localhost:5000/api/auth/check-security-answer', { username, securityAnswer });
            setStep(3);
        } catch (error) {
            alert('Incorrect security answer. Please try again.');
        }
    };

    const handleNewPasswordSubmit = async () => {
        try {
            await axios.post('http://localhost:5000/api/auth/reset-password', { username, newPassword });
            alert('Password reset successfully. Please login.');
            history.push('/login');
        } catch (error) {
            alert('Error resetting password. Please try again.');
        }
    };

    return (
        <div>
            {step === 1 && (
                <div>
                    <h2>Forgot Password</h2>
                    <input
                        type="text"
                        placeholder="Enter your username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <button onClick={handleUsernameSubmit}>Submit</button>
                </div>
            )}
            {step === 2 && (
                <div>
                    <h2>Security Question</h2>
                    <p>{securityQuestion}</p>
                    <input
                        type="text"
                        placeholder="Enter your answer"
                        value={securityAnswer}
                        onChange={(e) => setSecurityAnswer(e.target.value)}
                    />
                    <button onClick={handleSecurityAnswerSubmit}>Submit</button>
                </div>
            )}
            {step === 3 && (
                <div>
                    <h2>Reset Password</h2>
                    <input
                        type="password"
                        placeholder="Enter new password"
                        value={newPassword}
                        onChange={(e) => setNewPassword(e.target.value)}
                    />
                    <button onClick={handleNewPasswordSubmit}>Submit</button>
                </div>
            )}
        </div>
    );
};

export default ForgotPassword;
