import React, { useState } from 'react';
import axios from 'axios';

const AddWord = () => {
  const [formData, setFormData] = useState({
    english: '',
    turkish: '',
    sentences1: '',
    sentences2: '',
    sentences3: '',
    image: '',
    pronunciation: ''
  });

  const { english, turkish, sentences1, sentences2, sentences3, image, pronunciation } = formData;

  const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/words', formData);
      console.log(res.data);
    } catch (err) {
      console.error(err.response.data);
    }
  };

  return (
    <div>
      <h1>Add Word</h1>
      <form onSubmit={onSubmit}>
        <input type="text" name="english" value={english} onChange={onChange} placeholder="English Word" required />
        <input type="text" name="turkish" value={turkish} onChange={onChange} placeholder="Turkish Translation" required />
        <input type="text" name="sentences1" value={sentences1} onChange={onChange} placeholder="Sentence 1" required />
        <input type="text" name="sentences2" value={sentences2} onChange={onChange} placeholder="Sentence 2" required />
        <input type="text" name="sentences3" value={sentences3} onChange={onChange} placeholder="Sentence 3" required />
        <input type="text" name="image" value={image} onChange={onChange} placeholder="Image URL" />
        <input type="text" name="pronunciation" value={pronunciation} onChange={onChange} placeholder="Pronunciation (optional)" />
        <button type="submit">Add Word</button>
      </form>
    </div>
  );
};

export default AddWord;
